import React, { useState, useContext } from "react";
import { AuthContext } from "../Contexts/AuthContext";
import { Alert } from 'react-st-modal';
import ForgotPasswordForm from './ForgotPasswordForm';

function SignInForm() {
  const [state, setState] = useState({});
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const { setIsLogged } = useContext(AuthContext);

  const handleChange = (evt) => {
    const value = evt.target.value;
    setState({
      ...state,
      [evt.target.name]: value,
    });
  };

  const handleOnSubmit = (evt) => {
    evt.preventDefault();

    fetch("${process.env.REACT_APP_BASE_URL}/api/login", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(state),
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((loginSuccess) => {
        console.log("Login success==>:", loginSuccess);

        loginSuccess && setIsLogged(true);
        if (loginSuccess) Alert('Welcome ${state.username}');
        else Alert('INVALID ENTRY');
      })
      .catch((error) => {
        console.error("Login failed:", error);
      });

    const { username, password } = state;

    for (const key in state) {
      setState({
        ...state,
        [key]: "",
      });
    }

    fetch("${process.env.REACT_APP_BASE_URL}/api/" + username, {
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((data) => {
        localStorage.setItem("userId", data);
        console.log("data==>", data);
      })
      .catch((error) => {
        console.error("Login failed:", error);
      });
  };

  return (
    <div className="form-container sign-in-container">
      {showForgotPassword ? (
        <ForgotPasswordForm onClose={() => setShowForgotPassword(false)} />
      ) : (
        <form onSubmit={handleOnSubmit}>
          <h1>Sign in</h1>
          <br />
          <input
            type="text"
            name="username"
            placeholder="Enter Username"
            onChange={handleChange}
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            onChange={handleChange}
          />
          <a onClick={() => setShowForgotPassword(true)}>Forgot your password?</a>
          <button>Sign In</button>
        </form>
      )}
    </div>
  );
}

export default SignInForm;
